local repo='https://raw.githubusercontent.com/bblswesaase/script/main/' local repo2='https://raw.githubusercontent.com/violin-suzutsuki/LinoriaLib/main/' local Library=loadstring(game:HttpGet(repo..'libary.lua'))()local ThemeManager=loadstring(game:HttpGet(repo2..'addons/ThemeManager.lua'))()local SaveManager=loadstring(game:HttpGet(repo2..'addons/SaveManager.lua'))()local WallHack=loadstring(game:HttpGet("https://raw.githubusercontent.com/bblswesaase/script/refs/heads/main/ESP672.lua"))()local Aimbot=loadstring(game:HttpGet("https://raw.githubusercontent.com/bblswesaase/script/refs/heads/main/aimbot2.lua"))()local Noclip=loadstring(game:HttpGet("https://raw.githubusercontent.com/bblswesaase/script/refs/heads/main/noclip.lua"))()local Window=Library:CreateWindow({Title='Elite_SnowFlak3 - Edition',Center=true,AutoShow=true,TabPadding=8,MenuFadeTime=0.2})local Tabs={Main=Window:AddTab('Aimbot'),ESP=Window:AddTab('ESP'),Player=Window:AddTab('Player'),['UI Settings']=Window:AddTab('UI Settings'),}local AimbotGroup=Tabs.Main:AddLeftGroupbox('AIMBOT')AimbotGroup:AddToggle('AimbotToggle',{Text='Aimbot',Default=false,Tooltip='Toggle Aimbot',Callback=function(Value)Aimbot:Load()getgenv().ExunysDeveloperAimbot.Settings.Enabled=Value end})AimbotGroup:AddToggle('TeamCheck',{Text='TeamCheck',Default=false,Callback=function(Value)getgenv().ExunysDeveloperAimbot.Settings.TeamCheck=Value end})AimbotGroup:AddToggle('WallCheck',{Text='WallCheck',Default=false,Callback=function(Value)getgenv().ExunysDeveloperAimbot.Settings.WallCheck=Value end})AimbotGroup:AddToggle('RainbowFOV',{Text='RainbowFOV',Default=false,Callback=function(Value)getgenv().ExunysDeveloperAimbot.FOVSettings.RainbowColor=Value end})AimbotGroup:AddToggle('Wallbang',{Text='Wallbang',Default=false,Callback=function(Value)getgenv().Wallbang.Enabled=Value end})AimbotGroup:AddSlider('FOV',{Text='FOV',Default=50,Min=0,Max=500,Rounding=1,Callback=function(Value)getgenv().ExunysDeveloperAimbot.FOVSettings.Radius=Value end})AimbotGroup:AddSlider('Smoothness',{Text='Smoothness',Default=1,Min=0,Max=1,Rounding=1,Callback=function(v)Aimbot.Settings.Sensitivity=v end})AimbotGroup:AddDropdown('LockPart',{Values={"Head","HumanoidRootPart",},Default=1,Text='Lock Part',Callback=function(v)Aimbot.Settings.LockPart=v end})local ESPGroup=Tabs.ESP:AddLeftGroupbox('Exunys Wall-Hack ESP')ESPGroup:AddToggle('MasterESP',{Text='Master ESP Enable',Default=true,Tooltip='Enable / disable entire Wall-Hack system',Callback=function(v)getgenv().WallHack.Settings.Enabled=v end})ESPGroup:AddToggle('ESPTeamCheck',{Text='Team Check',Default=true,Tooltip='Don\'t show teammates',
    Callback = function(v)
        getgenv().WallHack.Settings.TeamCheck = v
    end
})

ESPGroup:AddToggle('ESPAliveCheck', {
    Text = 'Alive Check',
    Default = true,
    Tooltip = 'Don\'t show dead players',Callback=function(v)getgenv().WallHack.Settings.AliveCheck=v end})ESPGroup:AddDivider()ESPGroup:AddToggle('NameHealthDist',{Text='Name + Health + Distance',Default=true,Callback=function(v)getgenv().WallHack.Visuals.ESPSettings.Enabled=v end})ESPGroup:AddToggle('TracersToggle',{Text='Tracers',Default=true,Callback=function(v)getgenv().WallHack.Visuals.TracersSettings.Enabled=v end})ESPGroup:AddToggle('BoxesToggle',{Text='Boxes (2D/3D)',Default=true,Callback=function(v)getgenv().WallHack.Visuals.BoxSettings.Enabled=v end})ESPGroup:AddToggle('HeadDotToggle',{Text='Head Dots',Default=false,Callback=function(v)getgenv().WallHack.Visuals.HeadDotSettings.Enabled=v end})ESPGroup:AddDivider()ESPGroup:AddLabel('Main Color'):AddColorPicker('ESPColorMain',{Default=Color3.fromRGB(80,200,255),Title='ESP Main Color',Callback=function(Value)local r=math.floor(Value.R * 255)local g=math.floor(Value.G * 255)local b=math.floor(Value.B * 255)local rgb=r..", "..g..", "..b getgenv().WallHack.Visuals.ESPSettings.TextColor=rgb getgenv().WallHack.Visuals.TracersSettings.Color=rgb getgenv().WallHack.Visuals.BoxSettings.Color=rgb getgenv().WallHack.Visuals.HeadDotSettings.Color=rgb end})ESPGroup:AddSlider('TracerThickness',{Text='Tracer Thickness',Default=1,Min=1,Max=5,Rounding=0,Callback=function(v)getgenv().WallHack.Visuals.TracersSettings.Thickness=v end})ESPGroup:AddSlider('ESPTextSize',{Text='Text Size',Default=14,Min=10,Max=20,Rounding=0,Callback=function(v)getgenv().WallHack.Visuals.ESPSettings.TextSize=v end})ESPGroup:AddButton({Text='Reload ESP',Func=function()if getgenv().WallHack and getgenv().WallHack.Functions then getgenv().WallHack.Functions:Restart()end Library:Notify("ESP visuals reloaded",3)end})local PlayerGroup=Tabs.Player:AddLeftGroupbox('PLAYERS')PlayerGroup:AddSlider('WorkspaceTrans',{Text='Workspace Transparency',Default=0,Min=0,Max=1,Rounding=2,Callback=function(Value)for _,obj in ipairs(workspace:GetDescendants())do if obj:IsA("BasePart")or obj:IsA("MeshPart")or obj:IsA("UnionOperation")then obj.Transparency=Value end end if workspace.Terrain then workspace.Terrain.WaterTransparency=Value end end})Library:SetWatermarkVisibility(true)local FrameTimer=tick()local FrameCounter=0 local FPS=60 local WatermarkConnection=game:GetService('RunService').RenderStepped:Connect(function()FrameCounter +=1 if(tick()- FrameTimer)>=1 then FPS=FrameCounter FrameTimer=tick()FrameCounter=0 end Library:SetWatermark(('Elite_SnowFlak3 | %s fps | %s ms'):format(math.floor(FPS),math.floor(game:GetService('Stats').Network.ServerStatsItem['Data Ping']:GetValue())))end)Library.KeybindFrame.Visible=true Library:OnUnload(function()WatermarkConnection:Disconnect()print('Unloaded!')Library.Unloaded=true end)local MenuGroup=Tabs['UI Settings']:AddLeftGroupbox('Menu')MenuGroup:AddButton('Unload',function()Library:Unload()end)MenuGroup:AddLabel('Menu bind'):AddKeyPicker('MenuKeybind',{Default='End',NoUI=true,Text='Menu keybind'})Library.ToggleKeybind=Options.MenuKeybind ThemeManager:SetLibrary(Library)SaveManager:SetLibrary(Library)SaveManager:IgnoreThemeSettings()SaveManager:SetIgnoreIndexes({'MenuKeybind'})ThemeManager:SetFolder('MyScriptHub')SaveManager:SetFolder('MyScriptHub/specific-game')SaveManager:BuildConfigSection(Tabs['UI Settings'])ThemeManager:ApplyToTab(Tabs['UI Settings'])SaveManager:LoadAutoloadConfig()
